mapper.select:
  	
  		SELECT * FROM dnd.character
  		WHERE 1=1
  		AND type = 'B'
  		
		
 		AND user_alias IN
			
			 #{item.alias}
			
		
		
  		order by buff asc;

SELECT * FROM dnd.character WHERE 1=1 AND buff = 0 AND damage > 1000
AND user_alias IN
#{item.alias}
order by damage desc;

SELECT * FROM dnd.character WHERE 1=1 AND type = 'S' AND damage < 1000
AND user_alias IN
#{item.alias}
order by damage desc;

SELECT * FROM dnd.character WHERE 1=1 AND type = 'D' AND damage < 1000
AND user_alias IN
#{item.alias}
order by damage desc;

mapper.update:
UPDATE dnd.character SET damage = #{damage},buff = #{buff} 
where 1=1
and id = #{id}
